package com.ngarrache.katas.romannumerals

import spock.lang.Specification

/**
 * @author Nizar Garrache
 *
 * @since 10 juil. 2011
 */
class RomanNumeralsConverterTest extends Specification {
	def converter = new RomanNumeralsConverter()

	def "arabicToRoman should throw exception when number is negative" () {
		when:
		converter.arabicToRoman(-1)

		then:
		thrown(IllegalArgumentException)
	}

	def "arabicToRoman should return roman number"() {
		expect:
		converter.arabicToRoman(arabic) == roman

		where:
		arabic | roman
		0      | ""
		1 	   | "I"
		2      | "II"
		3      | "III"
		4      | "IV"
		5      | "V"
		8      | "VIII"
		9      | "IX"
		10     | "X"
		11     | "XI"
		14     | "XIV"
		15     | "XV"
		16     | "XVI"
		19     | "XIX"
		20     | "XX"
		24     | "XXIV"
		30     | "XXX"
		35     | "XXXV"
		39     | "XXXIX"
		40     | "XL"
		50     | "L"
		90     | "XC"
		102    | "CII"
		400    | "CD"
		500    | "D"
		556    | "DLVI"
		1003   | "MIII"
		1345   | "MCCCXLV"
		2945   | "MMCMXLV"
		3000   | "MMM"
		9999999| ""
	}
	
	def "romanToArabic should return arabic number"() {
		expect:
		converter.romanToArabic(roman) == arabic

		where:
		arabic | roman
		0      | ""
		1 	   | "I"
		2      | "II"
		3      | "III"
		4      | "IV"
		5      | "V"
		8      | "VIII"
		9      | "IX"
		10     | "X"
		11     | "XI"
		14     | "XIV"
		15     | "XV"
		16     | "XVI"
		19     | "XIX"
		20     | "XX"
		24     | "XXIV"
		30     | "XXX"
		35     | "XXXV"
		39     | "XXXIX"
		40     | "XL"
		50     | "L"
		90     | "XC"
		102    | "CII"
		400    | "CD"
		500    | "D"
		556    | "DLVI"
		1003   | "MIII"
		1345   | "MCCCXLV"
		2945   | "MMCMXLV"
		3000   | "MMM"
		9999999| ""
	}
}